/*! PXL loader (dual-source manifest, CDN-stale proof) */
(function(w,d){
  function load(u){var s=d.createElement("script");s.src=u;s.async=true;d.head.appendChild(s)}
  function use(src){ if(w.PXL_URL){ load(w.PXL_URL) } else { console.error("PXL: manifest missing PXL_URL from",src) } }
  var now=Date.now();
  var gh ="https://raw.githubusercontent.com/randyforseth-spec/side-hustle-builder-snippet/main/pixel-manifest.js?ts="+now;
  var jsd="https://cdn.jsdelivr.net/gh/randyforseth-spec/side-hustle-builder-snippet@main/pixel-manifest.js?ts="+now;
  var m=d.createElement("script");
  m.src=gh;
  m.onload=function(){ use(gh) };
  m.onerror=function(){ var f=d.createElement("script"); f.src=jsd; f.onload=function(){ use(jsd) }; f.onerror=function(e){ console.error("PXL: both manifests failed",e) }; d.head.appendChild(f) };
  d.head.appendChild(m);
})(window,document);
